mapper.insert:
		
			SELECT SEQ_APPLY.NEXTVAL FROM DUAL
		

		INSERT INTO TBL_APPLY(APPLY_NO
		, USER_NO
		, SUB_NO
		, AC_SCORE
		, SERIAL_NUM
		, AC_DATE
		, CONTENT
		)
		VALUES(#{applyNo}
		, #{userNo}
		, #{subNo}
		, #{acScore}
		, #{serialNum}
		, #{acDate}
		, #{content}
)

mapper.select:
SELECT * FROM( SELECT ROWNUM RNUM , APPLY_NO , USER_NO , USER_NAME , SUB_NAME , APPLY_DATE , APPLY_STATE FROM ( SELECT A.APPLY_NO , A.USER_NO , U.USER_NAME , S.SUB_NAME , A.APPLY_DATE , A.APPLY_STATE FROM TBL_APPLY A , TBL_SUBJECT S , TBL_USER U WHERE A.SUB_NO = S.SUB_NO AND U.USER_NO = A.USER_NO AND A.APPLY_NO > 0
ORDER BY A.APPLY_NO DESC, A.APPLY_DATE DESC
)) WHERE (RNUM >= #{pageStart} AND RNUM <= #{pageEnd})

SELECT SUB_NO
	FROM TBL_APPLY
	WHERE APPLY_NO = #{applyNo}

mapper.update:
UPDATE TBL_APPLY
	SET SUB_NO = #{subNo}
	, AC_SCORE = #{acScore}
	WHERE APPLY_NO = #{applyNo}

UPDATE TBL_APPLY
SET APPLY_STATE = '승인'
, REASON = #{reason}
, APPROVER = #{approver}
, APPROVAL_DATE = SYSDATE
WHERE APPLY_NO = #{applyNo}

UPDATE TBL_APPLY
SET APPLY_STATE = '거절'
, REASON = #{reason}
, APPROVER = #{approver}
, APPROVAL_DATE = SYSDATE
WHERE APPLY_NO = #{applyNo}

UPDATE TBL_APPLY
SET APPLY_STATE = '승인대기'
, APPROVER = #{approver}
, APPROVAL_DATE = SYSDATE
WHERE APPLY_NO = #{applyNo}

mapper.select:
SELECT * FROM(
SELECT ROWNUM RNUM, U.USER_NO , U.USER_NAME , A.SUBTOTAL , A.MISTOTAL , (SUBTOTAL+MISTOTAL) AS TOTAL FROM( SELECT USER_NO , SUM(SUBTOTAL) AS SUBTOTAL , SUM(MISTOTAL) AS MISTOTAL FROM( SELECT USER_NO , 0 AS SUBTOTAL , SUM(MISTOTAL) AS MISTOTAL FROM ( SELECT USER_NO, SUM(MIS_ATT_NO) AS MISTOTAL FROM ( SELECT USER_NO , COUNT(MIS_ATT_NO)*3 AS MIS_ATT_NO FROM TBL_MIS_ATT GROUP BY USER_NO UNION SELECT USER_NO , SUM(MIS_ATT_TIMES)*3 AS MIS_ATT_NO FROM TBL_MIS_SCORE GROUP BY USER_NO UNION SELECT USER_NO, 0 AS MIS_ATT_NO FROM TBL_USER WHERE AUTHORITY != 1 GROUP BY USER_NO ) GROUP BY USER_NO ) GROUP BY USER_NO UNION SELECT USER_NO , SUM(AC_SCORE) AS SUBTOTAL , 0 AS MISTOTAL FROM ( SELECT U.USER_NO , SUM(TOEIC) AS AC_SCORE FROM TBL_CONVERSION_SCORE C, TBL_APPLY A, TBL_SUBJECT S, TBL_USER U WHERE (A.USER_NO = U.USER_NO AND C.TOEFL = A.AC_SCORE AND A.SUB_NO = S.SUB_NO AND S.SUB_NAME = 'TOEFL') OR (A.USER_NO = U.USER_NO AND C.TEPS = A.AC_SCORE AND A.SUB_NO = S.SUB_NO AND S.SUB_NAME = 'TEPS') OR (A.USER_NO = U.USER_NO AND C.TOEIC_S = A.AC_SCORE AND A.SUB_NO = S.SUB_NO AND S.SUB_NAME = 'TOEIC Speaking') GROUP BY U.USER_NO UNION SELECT U.USER_NO , SUM(AC_SCORE) AS AC_SCORE FROM TBL_APPLY A, TBL_USER U WHERE A.USER_NO = U.USER_NO AND APPLY_STATE = '승인' GROUP BY U.USER_NO UNION SELECT U.USER_NO , -SUM(AC_SCORE) AS AC_SCORE FROM TBL_APPLY A, TBL_SUBJECT S, TBL_USER U WHERE A.USER_NO = U.USER_NO AND A.APPLY_STATE = '승인' AND( (A.SUB_NO = S.SUB_NO AND S.SUB_NAME = 'TOEFL') OR (A.SUB_NO = S.SUB_NO AND S.SUB_NAME = 'TEPS') OR (A.SUB_NO = S.SUB_NO AND S.SUB_NAME = 'TOEIC Speaking') ) GROUP BY U.USER_NO UNION SELECT USER_NO, 0 AS AC_SCORE FROM TBL_USER WHERE AUTHORITY != 1 GROUP BY USER_NO ) GROUP BY USER_NO UNION SELECT USER_NO, 0 AS SUBTOTAL, 0 AS MISTOTAL FROM TBL_USER WHERE AUTHORITY != 1 GROUP BY USER_NO ) GROUP BY USER_NO ORDER BY USER_NO ASC ) A , TBL_USER U WHERE A.USER_NO = U.USER_NO
) WHERE (RNUM >= #{pageStart} AND RNUM <= #{pageEnd}) ORDER BY USER_NO ASC

mapper.insert:
		INSERT INTO TBL_APPLY(APPLY_NO
		, USER_NO
		, SUB_NO
		,APPLY_STATE
		, APPLY_DATE
		,AC_SCORE
		)
		VALUES(SEQ_APPLY.NEXTVAL
		, #{userNo}
		, #{subNo}
		,'승인'
		, sysDate
		,#{acScore}
		)

